import borderImg from '../img/Rectangle39610.png';
import spaceImg from '../img/Rectangle39613.png';
import dreamersImg from '../img/Rectangle39625.png';
import fulfillImg from '../img/Rectangle39627.png';

export const offers = [
    {
        id: 'border',
        title: 'Move the borders of reality!',
        description: "Go on a space adventure - it's possible with us!",
        variant: 'wide',
        theme: 'nebula',
        bgImage: borderImg,
    },
    {
        id: 'space',
        title: 'Space is not just stars and planets',
        description: 'Go on a space adventure',
        variant: 'default',
        theme: 'onyx',
        bgImage: spaceImg,
    },
    {
        id: 'dreamers',
        title: 'For those who dream of stars',
        description: 'Our offer: make your dream come true',
        variant: 'default',
        theme: 'twilight',
        bgImage: dreamersImg,
    },
    {
        id: 'fulfill',
        title: 'Fulfill your fantastic dreams',
        description: 'Space has never been so close',
        variant: 'wide',
        theme: 'aurora',
        bgImage: fulfillImg,
    },
];
