import borderImg from '../img/Rectangle 39610.png';
import spaceImg from '../img/Rectangle 39613.png';
import dreamersImg from '../img/Rectangle 39625.png';
import fulfillImg from '../img/Rectangle 39627.png';

export const offers = [
    {
        id: 'border',
        title: 'Move the borders of reality!',
        description: "Go on a space adventure - it's possible with us!",
        variant: 'wide',
        theme: 'nebula',
        bgImage: borderImg,
        overlay:
            'linear-gradient(140deg, rgba(58, 28, 98, 0.8), rgba(148, 43, 127, 0.55) 55%, rgba(31, 41, 95, 0.85) 100%)',
    },
    {
        id: 'space',
        title: 'Space is not just stars and planets',
        description: 'Go on a space adventure',
        variant: 'default',
        theme: 'onyx',
        bgImage: spaceImg,
        overlay:
            'linear-gradient(160deg, rgba(21, 25, 36, 0.85), rgba(28, 45, 42, 0.6) 60%, rgba(18, 17, 23, 0.85) 95%)',
    },
    {
        id: 'dreamers',
        title: 'For those who dream of stars',
        description: 'Our offer: make your dream come true',
        variant: 'default',
        theme: 'twilight',
        bgImage: dreamersImg,
        overlay:
            'linear-gradient(140deg, rgba(26, 33, 68, 0.82), rgba(10, 47, 79, 0.55) 55%, rgba(31, 23, 72, 0.85) 95%)',
    },
    {
        id: 'fulfill',
        title: 'Fulfill your fantastic dreams',
        description: 'Space has never been so close',
        variant: 'wide',
        theme: 'aurora',
        bgImage: fulfillImg,
        overlay:
            'linear-gradient(150deg, rgba(19, 37, 78, 0.82), rgba(39, 79, 104, 0.6) 55%, rgba(74, 23, 114, 0.85) 95%)',
    },
];
